// Push notification handling
self.addEventListener('push', (event) => {
  console.log('[SW] Push notification received');
  
  let notificationData = {
    title: 'The JERK Tracker',
    body: 'You have a new notification',
    icon: '/icons/icon-192x192.svg',
    badge: '/icons/icon-72x72.svg',
    tag: 'jerk-tracker-notification',
    data: {}
  };

  if (event.data) {
    try {
      const payload = event.data.json();
      notificationData = {
        ...notificationData,
        ...payload
      };
    } catch (error) {
      console.error('[SW] Error parsing push data:', error);
    }
  }

  const options = {
    body: notificationData.body,
    icon: notificationData.icon,
    badge: notificationData.badge,
    tag: notificationData.tag,
    data: notificationData.data,
    actions: getNotificationActions(notificationData.type),
    vibrate: [200, 100, 200],
    requireInteraction: notificationData.requireInteraction || false
  };

  event.waitUntil(
    self.registration.showNotification(notificationData.title, options)
  );
});

// Notification click handling
self.addEventListener('notificationclick', (event) => {
  console.log('[SW] Notification clicked:', event.notification.tag);
  
  event.notification.close();

  const notificationData = event.notification.data;
  let targetUrl = '/';

  // Determine target URL based on notification type
  switch (notificationData.type) {
    case 'new-order':
      targetUrl = notificationData.role === 'driver' ? '/driver' : '/admin';
      break;
    case 'order-update':
      targetUrl = `/orders/${notificationData.orderId}`;
      break;
    case 'driver-assigned':
      targetUrl = '/customer';
      break;
    case 'delivery-complete':
      targetUrl = '/customer';
      break;
    default:
      targetUrl = getDashboardUrl(notificationData.role);
  }

  // Handle action buttons
  if (event.action) {
    switch (event.action) {
      case 'view-order':
        targetUrl = `/orders/${notificationData.orderId}`;
        break;
      case 'accept-order':
        // This would trigger an API call to accept the order
        targetUrl = '/driver';
        break;
      case 'dismiss':
        return; // Just close, don't open anything
    }
  }

  event.waitUntil(
    clients.matchAll({ type: 'window', includeUncontrolled: true }).then((clientList) => {
      // Try to focus existing window
      for (const client of clientList) {
        if (client.url === targetUrl && 'focus' in client) {
          return client.focus();
        }
      }
      
      // Open new window if no existing one found
      if (clients.openWindow) {
        return clients.openWindow(targetUrl);
      }
    })
  );
});

// Get notification actions based on type
function getNotificationActions(type) {
  switch (type) {
    case 'new-order':
      return [
        { action: 'accept-order', title: 'Accept Order', icon: '/icons/icon-32x32.svg' },
        { action: 'view-order', title: 'View Details', icon: '/icons/icon-32x32.svg' }
      ];
    case 'order-update':
      return [
        { action: 'view-order', title: 'View Order', icon: '/icons/icon-32x32.svg' }
      ];
    default:
      return [
        { action: 'view-order', title: 'View', icon: '/icons/icon-32x32.svg' },
        { action: 'dismiss', title: 'Dismiss', icon: '/icons/icon-32x32.svg' }
      ];
  }
}

// Get dashboard URL based on user role
function getDashboardUrl(role) {
  switch (role) {
    case 'admin':
      return '/admin';
    case 'manager':
      return '/manager';
    case 'driver':
      return '/driver';
    case 'customer':
      return '/customer';
    default:
      return '/';
  }
}

// Handle push subscription changes
self.addEventListener('pushsubscriptionchange', (event) => {
  console.log('[SW] Push subscription changed');
  
  event.waitUntil(
    // Re-subscribe with new subscription
    self.registration.pushManager.subscribe({
      userVisibleOnly: true,
      applicationServerKey: urlBase64ToUint8Array(
        process.env.NEXT_PUBLIC_VAPID_PUBLIC_KEY || ''
      )
    }).then((newSubscription) => {
      // Send new subscription to server
      return fetch('/api/push/subscribe', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(newSubscription)
      });
    })
  );
});

// Utility function to convert VAPID key
function urlBase64ToUint8Array(base64String) {
  const padding = '='.repeat((4 - base64String.length % 4) % 4);
  const base64 = (base64String + padding)
    .replace(/-/g, '+')
    .replace(/_/g, '/');

  const rawData = atob(base64);
  const outputArray = new Uint8Array(rawData.length);

  for (let i = 0; i < rawData.length; ++i) {
    outputArray[i] = rawData.charCodeAt(i);
  }
  return outputArray;
}

